# create corpus
b2 <- Corpus(VectorSource(my_text))

# preprocess corpus
b2 <- tm_map(b2, stripWhitespace)
b2 <- tm_map(b2, content_transformer(tolower))
b2 <- tm_map(b2, removeWords, stopwords("english"))
b2 <- tm_map(b2, removePunctuation)
b2 <- tm_map(b2, PlainTextDocument)

# create a term-document matrix
tdm2 <- TermDocumentMatrix(b2)
m2 <- as.matrix(tdm2)
v2 <- sort(rowSums(m2), decreasing = TRUE)

# create a data frame of the most frequent words
d3 <- data.frame(word = names(v2), freq = v2)
d4 <- subset(d3, d3$freq>28)

# create a word cloud
wordcloud(d4$word, d4$freq)
